import * as actionTypes from './actions.js';

const initialState = {
  openTabs: {},
}

const reducer = (state = initialState, action) => {
  switch(action.type) {
    case actionTypes.TOGGLE:
      if(action.payload.props.allowMultipleOpen) {
        return {
          ...state.openTabs,
          [action.payload.label]: !action.payload.props.isOpen
        }
      }
  }
