import React from 'react';
import Accordion from './Component/Accordion';

const products = [
  {
    label: 'label1',
    title: 'Title 1',
    detsil: 'Detail 1', 
  },
  {
    label: 'label2',
    title: 'Title 2',
    detsil: 'Detail 2', 
  },
  {
    label: 'label3',
    title: 'Title 3',
    detsil: 'Detail 3', 
  },
  {
    label: 'label4',
    title: 'Title 4',
    detsil: 'Detail 4', 
  },
]

function App() {
  return (
    <div>
      <h1>Accordion Demo</h1>
      <Accordion>
        {/* <div label='Alligator Mississippiensis'>
