const initialState = {
  openTabs: {},
}

const reducer = (state = initialState, action) => {
  switch(action.type) {
    case actionTypes.TOGGLE:
      if(action.payload.props.allowMultipleOpen) {
        return {
          ...state.openTabs,
          [label]: !props.isOpen
        }
      }
  }
  onClick = label => {
    const {
      props: { allowMultipleOpen },
      state: { openTabs },
    } = this;

    const isOpen = !!openTabs[label];

    if (allowMultipleOpen) {
      this.setState({
        openTabs: {
          ...openTabs,
          [label]: !isOpen
        }
      });
    } else {
      this.setState({
        openTabs: {
          [label]: !isOpen
        }
      });
    }
  };
  return state;
}

export default reducer;
